// –ü–æ–ª—É—á–∞–µ–º –∫—É—Ä—Å—ã –∏–∑ API
const rates = $input.item.json.rates;
const date = $input.item.json.date;

// –§–æ—Ä–º–∏—Ä—É–µ–º –º–∞—Å—Å–∏–≤ –¥–ª—è –≤—Å—Ç–∞–≤–∫–∏ –≤ –ë–î
const exchangeRates = [];

// –ö—É—Ä—Å—ã –æ—Ç KGS –∫ –¥—Ä—É–≥–∏–º –≤–∞–ª—é—Ç–∞–º
const currencies = ['USD', 'EUR', 'RUB'];

for (const currency of currencies) {
  if (rates[currency]) {
    // –ü—Ä—è–º–æ–π –∫—É—Ä—Å (KGS -> –≤–∞–ª—é—Ç–∞)
    exchangeRates.push({
      date: new Date().toISOString().split('T')[0],
      from_currency: 'KGS',
      to_currency: currency,
      rate: rates[currency],
      source: 'ExchangeRate-API'
    });

    // –û–±—Ä–∞—Ç–Ω—ã–π –∫—É—Ä—Å (–≤–∞–ª—é—Ç–∞ -> KGS)
    exchangeRates.push({
      date: new Date().toISOString().split('T')[0],
      from_currency: currency,
      to_currency: 'KGS',
      rate: 1 / rates[currency],
      source: 'ExchangeRate-API'
    });
  }
}

// –î–æ–±–∞–≤–ª—è–µ–º –∫—É—Ä—Å KGS -> KGS = 1
exchangeRates.push({
  date: new Date().toISOString().split('T')[0],
  from_currency: 'KGS',
  to_currency: 'KGS',
  rate: 1.0,
  source: 'SYSTEM'
});

// –ö—Ä–æ—Å—Å-–∫—É—Ä—Å—ã –º–µ–∂–¥—É USD, EUR, RUB
if (rates.USD && rates.EUR) {
  // USD <-> EUR
  exchangeRates.push({
    date: new Date().toISOString().split('T')[0],
    from_currency: 'USD',
    to_currency: 'EUR',
    rate: rates.EUR / rates.USD,
    source: 'ExchangeRate-API_CROSS'
  });
  exchangeRates.push({
    date: new Date().toISOString().split('T')[0],
    from_currency: 'EUR',
    to_currency: 'USD',
    rate: rates.USD / rates.EUR,
    source: 'ExchangeRate-API_CROSS'
  });
}

if (rates.USD && rates.RUB) {
  // USD <-> RUB
  exchangeRates.push({
    date: new Date().toISOString().split('T')[0],
    from_currency: 'USD',
    to_currency: 'RUB',
    rate: rates.RUB / rates.USD,
    source: 'ExchangeRate-API_CROSS'
  });
  exchangeRates.push({
    date: new Date().toISOString().split('T')[0],
    from_currency: 'RUB',
    to_currency: 'USD',
    rate: rates.USD / rates.RUB,
    source: 'ExchangeRate-API_CROSS'
  });
}

if (rates.EUR && rates.RUB) {
  // EUR <-> RUB
  exchangeRates.push({
    date: new Date().toISOString().split('T')[0],
    from_currency: 'EUR',
    to_currency: 'RUB',
    rate: rates.RUB / rates.EUR,
    source: 'ExchangeRate-API_CROSS'
  });
  exchangeRates.push({
    date: new Date().toISOString().split('T')[0],
    from_currency: 'RUB',
    to_currency: 'EUR',
    rate: rates.EUR / rates.RUB,
    source: 'ExchangeRate-API_CROSS'
  });
}

return exchangeRates.map(rate => ({ json: rate }));

// –ü–æ–¥—Å—á—ë—Ç —Å—Ç–∞—Ç–∏—Å—Ç–∏–∫–∏
const items = $input.all();
const totalRates = items.length;
const currencies = new Set();

items.forEach(item => {
  currencies.add(item.json.from_currency);
  currencies.add(item.json.to_currency);
});

const summary = {
  date: new Date().toISOString().split('T')[0],
  total_rates: totalRates,
  currencies: Array.from(currencies).join(', '),
  status: 'success',
  timestamp: new Date().toISOString()
};

return [{ json: summary }];
