const items = $input.all();
const action = $('Categories Webhook').first().json.body.action;

let result = {};

if (action === 'list') {
  const categories = items.map(item => ({
    name: item.json.name,
    icon: item.json.icon,
    type: item.json.type,
    is_default: item.json.is_default
  }));

  const expenses = categories.filter(c => c.type === 'expense');
  const income = categories.filter(c => c.type === 'income');

  result = {
    success: true,
    expenses: expenses.map(c => `${c.icon} ${c.name}${c.is_default ? '' : ' (—Å–≤–æ–π)'}`).join(', '),
    income: income.map(c => `${c.icon} ${c.name}${c.is_default ? '' : ' (—Å–≤–æ–π)'}`).join(', '),
    total: categories.length
  };
} else if (action === 'add') {
  if (items.length > 0 && items[0].json.id) {
