parameters.query:
=DELETE FROM debts
WHERE id = {{ $json.body.debt_id }} AND user_id = {{ $json.body.user_id }}
RETURNING id, person_name, 'deleted' as status

parameters.jsCode:
// –§–æ—Ä–º–∞—Ç–∏—Ä—É–µ–º —Ä–µ–∑—É–ª—å—Ç–∞—Ç –¥–ª—è –¥–æ–ª–≥–æ–≤
const items = $input.all();
const action = $('Debts Webhook').first().json.body.action;

let result = {};

if (action === 'list') {
  const debts = items.map(item => item.json);
  const iOwe = debts.filter(d => d.debt_type === 'received' && !d.is_settled);
  const owedToMe = debts.filter(d => d.debt_type === 'given' && !d.is_settled);
  const settled = debts.filter(d => d.is_settled);

  result = {
    success: true,
    i_owe: iOwe.map(d => `${d.person_name}: ${d.remaining_amount} ${d.currency}${d.due_date ? ' (–¥–æ ' + new Date(d.due_date).toLocaleDateString('ru') + ')' : ''}`).join('; ') || '–Ω–µ—Ç',
    owed_to_me: owedToMe.map(d => `${d.person_name}: ${d.remaining_amount} ${d.currency}${d.due_date ? ' (–¥–æ ' + new Date(d.due_date).toLocaleDateString('ru') + ')' : ''}`).join('; ') || '–Ω–µ—Ç',
    settled_count: settled.length,
    total_i_owe: iOwe.reduce((s, d) => s + parseFloat(d.remaining_amount), 0),
    total_owed_to_me: owedToMe.reduce((s, d) => s + parseFloat(d.remaining_amount), 0)
  };
} else if (action === 'add') {
  if (items.length > 0 && items[0].json.id) {
    const d = items[0].json;
    const typeText = d.debt_type === 'received' ? '–Ø –¥–æ–ª–∂–µ–Ω' : '–ú–Ω–µ –¥–æ–ª–∂–µ–Ω';
    result = {
      success: true,
      message: `${typeText} ${d.person_name}: ${d.original_amount} ${d.currency}`
    };
  } else {
    result = { success: false, message: '–ù–µ —É–¥–∞–ª–æ—Å—å –¥–æ–±–∞–≤–∏—Ç—å –¥–æ–ª–≥' };
  }
} else if (action === 'pay') {
  if (items.length > 0 && items[0].json.id) {
    const d = items[0].json;
    result = {
      success: true,
      message: d.is_settled ? `–î–æ–ª–≥ ${d.person_name} –ø–æ–ª–Ω–æ—Å—Ç—å—é –ø–æ–≥–∞—à–µ–Ω!` : `–û–ø–ª–∞—á–µ–Ω–æ. –û—Å—Ç–∞–ª–æ—Å—å: ${d.remaining_amount}`
    };
  } else {
    result = { success: false, message: '–î–æ–ª–≥ –Ω–µ –Ω–∞–π–¥–µ–Ω' };
  }
} else if (action === 'settle') {
  if (items.length > 0 && items[0].json.id) {
    result = { success: true, message: `–î–æ–ª–≥ ${items[0].json.person_name} –∑–∞–∫—Ä—ã—Ç` };
  } else {
    result = { success: false, message: '–î–æ–ª–≥ –Ω–µ –Ω–∞–π–¥–µ–Ω' };
  }
} else if (action === 'delete') {
  if (items.length > 0 && items[0].json.id) {
    result = { success: true, message: `–î–æ–ª–≥ ${items[0].json.person_name} —É–¥–∞–ª—ë–Ω` };
  } else {
    result = { success: false, message: '–î–æ–ª–≥ –Ω–µ –Ω–∞–π–¥–µ–Ω' };
