// –§–æ—Ä–º–∞—Ç–∏—Ä—É–µ–º —Ä–µ–∑—É–ª—å—Ç–∞—Ç –¥–ª—è —Ä–µ–≥—É–ª—è—Ä–Ω—ã—Ö –ø–ª–∞—Ç–µ–∂–µ–π
const items = $input.all();
const action = $('Recurring Webhook').first().json.body.action;

let result = {};

const freqNames = {
  'daily': '–µ–∂–µ–¥–Ω–µ–≤–Ω–æ',
  'weekly': '–µ–∂–µ–Ω–µ–¥–µ–ª—å–Ω–æ', 
  'monthly': '–µ–∂–µ–º–µ—Å—è—á–Ω–æ',
  'yearly': '–µ–∂–µ–≥–æ–¥–Ω–æ'
};

if (action === 'list') {
  const payments = items.map(item => item.json);
  const active = payments.filter(p => p.is_active);
  const paused = payments.filter(p => !p.is_active);

  result = {
    success: true,
    active: active.map(p => `${p.category_icon || 'üìÖ'} ${p.title}: ${p.amount} ${p.currency} (${freqNames[p.frequency] || p.frequency}, —Å–ª–µ–¥: ${new Date(p.next_payment_date).toLocaleDateString('ru')})`).join('; ') || '–Ω–µ—Ç –∞–∫—Ç–∏–≤–Ω—ã—Ö',
    paused: paused.map(p => `${p.title} (–ø—Ä–∏–æ—Å—Ç–∞–Ω–æ–≤–ª–µ–Ω)`).join('; ') || '–Ω–µ—Ç',
    total_monthly: active.filter(p => p.frequency === 'monthly').reduce((s, p) => s + parseFloat(p.amount), 0),
    count: active.length
  };
} else if (action === 'add') {
  if (items.length > 0 && items[0].json.id) {
    const p = items[0].json;
    result = {
      success: true,
      message: `–î–æ–±–∞–≤–ª–µ–Ω —Ä–µ–≥—É–ª—è—Ä–Ω—ã–π –ø–ª–∞—Ç—ë–∂ "${p.title}": ${p.amount} ${p.currency} ${freqNames[p.frequency] || p.frequency}`
    };
  } else {
    result = { success: false, message: '–ù–µ —É–¥–∞–ª–æ—Å—å –¥–æ–±–∞–≤–∏—Ç—å' };
  }
} else if (action === 'pay') {
  if (items.length > 0 && items[0].json.id) {
    const p = items[0].json;
    result = {
      success: true,
      message: `–ü–ª–∞—Ç—ë–∂ "${p.title}" –æ—Ç–º–µ—á–µ–Ω. –°–ª–µ–¥—É—é—â–∏–π: ${new Date(p.next_payment_date).toLocaleDateString('ru')}`
    };
  } else {
    result = { success: false, message: '–ü–ª–∞—Ç—ë–∂ –Ω–µ –Ω–∞–π–¥–µ–Ω' };
  }
} else if (action === 'pause') {
  if (items.length > 0 && items[0].json.id) {
    const p = items[0].json;
    result = {
      success: true,
      message: p.is_active ? `–ü–ª–∞—Ç—ë–∂ "${p.title}" –≤–æ–∑–æ–±–Ω–æ–≤–ª—ë–Ω` : `–ü–ª–∞—Ç—ë–∂ "${p.title}" –ø—Ä–∏–æ—Å—Ç–∞–Ω–æ–≤–ª–µ–Ω`
    };
  } else {
